package game_def;

// 客户端设备类型
enum ClientDeviceType
{
	CDT_Unknown = 0;
	CDT_IOS = 1;
	CDT_Android = 2;
	CDT_WebPlayer = 3;
	CDT_Windows = 4;
	CDT_MacOS = 5;
}

enum PlayerIdentity
{
	PI_NormalPlayer = 1;
	PI_AgentManager = 2;
	PI_Agent = 3;
	PI_ClubManager = 4;
	PI_ClubCreator = 5;
}

enum PlayerAttrType
{
	PAT_CanCreateClub = 1;
	PAT_Password = 2;		            // 
};

enum MemberState
{
	MS_Offline = 0;
	MS_Online = 1;
	MS_InGame = 2;
};


enum ErrorCode
{
	EC_Unknown		= -1;		        //未知错误
	EC_Success		= 0 ;			//有时候内嵌在其他消息里面用，但是一般不用这个，如果成功的话
	EC_LackDiamond		= 1;			
	EC_LackGold		= 2;
	EC_LackRoomTicket	= 3;
	EC_WrongTargetAccount	= 4;			//输入的帐号有误
	EC_WrongCurrencyType	= 5;			//错误的资源类型
	EC_LackCurrency		= 6;			//资源不足
	EC_CurrencyMax		= 7;			//对方资源达到上限
	EC_DailyAwarded		= 8;			//已经领取过每日金币奖励了
	EC_PlayerNotOnline	= 9;			//玩家不在线
	EC_MessageNotRight	= 10;			//消息ID不存在或者本消条消息已过期
	EC_InOtherRoom		= 32;			//正在房间中
	EC_WrongRoomType	= 33;			//错误的房间类型

	EC_AgentHasUser		= 37;			//不能取消代理，因为他下面有玩家
	EC_SelfProportionLow = 38;			//不能设置超过自己的比例

	EC_WrongRoomId		= 42;			//错误的房间ID
	EC_JoinRoomDiamond	= 43;			//加入房间 ，金币不足
	EC_JoinRoomCoin		= 44;			//加入房间， 金币不足

	EC_TalkTooFast		= 50;			//说话太快		
	EC_MaxClubCount     = 51;   		//俱乐部数量达到上限
	EC_ApplyHandled 	= 52;			//消息已经被处理
	EC_PlayerAlreadyIn  = 53;			//玩家已经在本俱乐部里面
	EC_PlayerIsAgent    = 54;			//玩家是代理不能删除
	EC_ApplyRepeated 	= 55;			//玩家重复申请，请不要重复申请，耐心等待盟主审核。

	EC_ReadyDiamond = 60;               // 准备时金币不足
	EC_ReadyCoin    = 61;               // 准备时金币不足
	EC_CannotFindSuitableRoom	= 62;	//没有合适的房间
	EC_BeReadyTimeOut	= 63;		//准备超时，踢出房间
	EC_ServerPrepareStop	= 64;	//准备关服状态，无法进行相关操作
	EC_CannotPutSafeboxInRoom	= 66;  //房间内不能存款
	EC_DzReadyTime	= 67;	//斗地主未按时准备
	EC_RfReadyTime	= 68;	//跑得快未按时准备
	EC_ChangeRoomFail = 69;	// 换桌失败
	EC_CantReady = 70;		// 暂时不能准备

	EC_RoomFull = 80;	//房间人数已满
	EC_RoomDestory = 81; //房间已经不存在了

	EC_HasSpecialChar	= 92;		//名字或者标记里面含有特殊字符
	EC_ContentLength	= 93;		//输入的名字或者内容太长
	EC_GameMaintenance	= 95;		//游戏维护中
};


enum GameType
{
	GT_mj=1;//
	GT_pdk=2;//
	GT_niuniu = 3;	// 
	GT_dizhu = 4;	//
}

enum AAOption
{
	AAO_AA			= 1;
	AAO_DaYingJia	= 2;
}

enum PayOption
{
	PO_IndeedValue			= 1;
	PO_Proportion		= 2;
}
