package rpc;

enum MsgTarget
{
	MT_Gate = 1;
	MT_AccountServer = 2;
	MT_PaymentServer = 3;
	MT_Client = 4;
	MT_GameServer = 5;
	MT_CenterServer = 6;
};

// 客户端发来的消息，由Gate转发给内部服务器
message TM_Packet
{
	required int32 sckUniID = 1;
	required bytes msgBytes = 2;
	optional string ipAddress = 3 [default = ""];
};

// 从内部服务器发向Gate的，需要转发的消息
message TM_PacketEx
{
	required MsgTarget target = 1;  // 指定转发的目标端
	optional int32 targetID = 2 [ default = -1];
	required bytes msgBytes = 3;
	repeated int32 sckUniID = 4;	
	optional string ipAddress = 5 [default = ""];
};
