package rpc;
import "rpc_room.proto";
import "rpc_club.proto";

enum RFCardType
{
	RFCT_ERROR = -1;                                   //错误类型
	RFCT_SINGLE = 1;                                   //单牌类型
	RFCT_DOUBLE = 2;                                   //对牌类型
	RFCT_SINGLE_LINE = 3;                              //单连类型
	RFCT_DOUBLE_LINE = 4;                              //对连类型
	RFCT_THREE_TAKE_TWO = 5;                           //三带一对
	RFCT_THREE_TAKE_TWO_LINE = 6;                     //飞机带翅膀 对牌
	RFCT_THREE_TAKE_NONE = 7;      		               //三不带
	RFCT_THREE_TAKE_ONE_FINISH = 8;      		        //最后一手出完 三代一
	RFCT_FOUR_TAKE_THREE = 9;                           //四带三张
	RFCT_BOMB_CARD = 10;                               //炸弹类型
}
enum RFPiaoOption {
  RFPO_None = 0;
  RFPO_Piao1 = 1;
  RFPO_Piao2 = 2;
  RFPO_Once = 3;
  RFPO_Every = 4;
};

enum ShouZhuangOption
{
	SZO_XingYunFanPai = 1;
	SZ0_HeiTao3 = 2;
};
enum RFParamName
{
    RFPN_AAOption = 0;
    RFPN_PayOption = 1;
    RFPN_PayValue = 2;
    RFPN_Piao = 3;
    RFPN_ZongJuShu = 4;
    RFPN_4dai3 = 5;
    RFPN_15 = 6;
    RFPN_HongTao10 = 7;
	RFPN_FanChun = 8;
	RFPN_AAAZha = 9;
	RFPN_ShouZhungOption = 10;
}

//房间状态
enum RFRoomState
{
    RFRS_WAITSTART   = 0;   //等待开始
	RFRS_XUANPIAO   = 1;   //选漂
    RFSR_PLAYING     = 2;    //游戏中
}

//信息
message RFActionInfo
{
    optional int32 playerID = 1;
    required int32 endtime = 2;
	required int32 position = 3;
}

// 系统
message S2C_RFSendCard
{
	repeated int32 cards = 1;
	required int32 zhuangPostion = 2;
	optional int32 playTimes = 3;           //八局中的第几把
	required int32 timeStamp = 4;
    repeated UpdatePlayerInfo changeInfos = 5;
}

message RFPlayCar
{
    repeated int32 cards = 1;
    optional RFCardType cardType = 2;
    optional int32 maxCard = 3;
    optional int32 playerID = 4;
	optional int32 position = 5;
}

//出牌
message C2S_RFPlayCars
{
	required RFPlayCar playCard = 1;
}

//炸弹结算
message BoomCalculateInfo
{
	optional int32 playerID = 1;
	optional int32 winBoomScore = 2;
	optional int32 score = 3;
	optional int32 position = 5; 
}

//出牌广播
message S2C_RFPlayCars
{
    optional RFActionInfo action = 1;
    optional RFPlayCar playCard = 2;
	repeated BoomCalculateInfo bcInfo = 3;
	repeated RFActionInfo guo = 4;
}

message RFEndInfo
{
	required int32 playerID = 1;
	repeated int32 leftCards = 2;
	repeated int32 chuPai = 3;
	required int32 winScore =4;
	required int32 boomScore =5;
	required int32 score = 6;
	optional bool beiGuan = 7;
	optional bool hongTao10 = 8;
	required int32 position = 9;
	repeated int32 thirdCards = 10;
	optional bool zhuaNiao = 11;
}

//结算
message S2C_RFEnd
{
	required int32 playTimes = 1;  //jushu
	repeated RFEndInfo endInfo = 2;
	optional bool  fanChun = 3;
}

message TotalWinItem
{
	required int32 playerID = 1; //玩家id
	required int32 position = 2;	// 位置
	required int32 winScore =3; // 总共输赢
	required int32 boomScore =4; // 炸弹总分
	required int32 score = 5;	 // 当前分数
	required int32 totalWinTimes = 6; // 胜利次数
}

message S2C_TotalEnd
{
	repeated TotalWinItem totalItems = 1;  //玩家数据
	required int32 endTime = 2;  //结算时间
	required int32 totalJushu = 3; //总局数
	required int32 roomId = 4; //房间id
}

message RFPlayerInfo
{
    required int32 position = 1;  //位置
    required int32 state = 2;
    required int32 score = 3;
    required string name = 4;
    required string icon = 5;
    optional int32 endTime = 6;  //倒计时结束时间戳
    required bool delegate = 7;  //是否托管
    optional int32 piao = 8;
	optional int32 cardCount =9;
	optional int32 playerId = 10;
	optional int32 selecting = 11; //是否选漂中(0:未选漂 1:选漂中)
	optional bool online = 12;//玩家状态(默认为空或flase,如果是true就是离线)
}

// 数据同步
message S2C_RFRoomInfo
{
	required GameItem param = 1;
	required int32 roomState = 2;
	repeated RFPlayerInfo playerInfos = 3;
	optional int32 pan = 4;  //第几pan
    required int32 roomId = 5;
    repeated int32 shoupai = 6;
	optional RFPlayCar lastCard = 7;
}

message S2C_NewRFPlayerEnter
{
    required RFPlayerInfo pi=  1;
}

message C2S_MakeCard 
{
	repeated int32 ids = 1;
}
