package rpc;

// 点数定义
enum CardPoint
{
	CP_Two = 2;
	CP_Three = 3;
	CP_Four = 4;
	CP_Five = 5;
	CP_Six = 6;
	CP_Seven = 7;
	CP_Eight = 8;
	CP_Nine = 9;
	CP_Ten = 10;
	CP_J = 11;
	CP_Q = 12;
	CP_K = 13;
	CP_A = 14;
}

enum ExpectPaiXin
{
	EPX_LittleDanPai = 1;
	EPX_BigDanPai = 2;
	EPX_LitteDuiZi  = 3;
	EPX_BigDuiZi = 4;
	EPX_LittleShunZi = 5;
	EPX_BigShunZi = 6;
	EPX_JinHua = 7;
	EPX_TongHua = 8;
	EPX_BaoZi = 9;
	EPX_Men	= 10;
}

// 地主点数
enum DzCardPoint
{
	DCP_Three = 3;
	DCP_Four = 4;
	DCP_Five = 5;
	DCP_Six = 6;
	DCP_Seven = 7;
	DCP_Eight = 8;
	DCP_Nine = 9;
	DCP_Ten = 10;
	DCP_J = 11;
	DCP_Q = 12;
	DCP_K = 13;
	DCP_A = 14;
	DCP_Two = 16;
	DCP_Small = 18;
	DCP_Big = 20;
}

// 跑得快点数
enum RfCardPoint
{
	RCP_Three = 3;
	RCP_Four = 4;
	RCP_Five = 5;
	RCP_Six = 6;
	RCP_Seven = 7;
	RCP_Eight = 8;
	RCP_Nine = 9;
	RCP_Ten = 10;
	RCP_J = 11;
	RCP_Q = 12;
	RCP_K = 13;
	RCP_A = 14;
	RCP_Two = 16;
}

// 花色定义
enum HuaSe
{
	HS_FangKuai = 1;
	HS_MeiHua = 2;
	HS_HongTao = 3;
	HS_HeiTao = 4;
}

// 小大王
enum CardKing
{
	CK_Small = 52;
	CK_Big = 53;
}

// 牌ID列表:
// 0 - 12
// 13 - 25
// 26 - 38
// 39 - 51
// 牌ID = (花色-1) * 13 + 点数-2
// 花色 = ceil((牌ID+1) / 13)
// 点数 = (牌ID) % 13 + 2

// 多张牌
message PokerCards
{
	repeated int32 card = 1;
}
