package rpc;
import "game_def.proto";

message C2S_CreateClub
{
	required string name = 1;
};

message S2C_CreateClub
{
	required game_def.ErrorCode errorCode = 1;
	required string name = 2;
	required int32 clubId = 3;
};

message GameItem
{
	optional int32 id = 1;
	optional int32 clubId = 2;
	optional int32 gameType = 3;
	optional string name = 4;
	optional int32 radix = 5;
	optional int32 joinLimit = 6;
	optional int32 leavelimit = 7;
	optional int32 juShu = 8;
	optional int32 playerCount = 9;
	repeated int32 param = 10;
	optional bool  match = 11;
	optional int32 matchPlayerCount = 12;//匹配人数
}

message C2S_CreateGame
{
	optional GameItem gi = 1;
}

message S2C_CreateGame
{
	required game_def.ErrorCode errorCode = 1;
	optional GameItem gi = 2;
}

