message PlayerItem
{
	optional int32 id = 1;
	optional string name = 2;
	optional string icon = 3;
	optional bool ready = 4;
}

message DeskItem
{
	required int32  roomId = 1;
	required int32 gameId = 2;
	repeated PlayerItem pis = 3;
	optional int32 playTimes = 4;
}

message C2S_EnterClub
{
	required int32 clubId = 1;
};

message S2C_EnterClub
{
	required int32 clubId = 1;
	optional string name =2;		//俱乐部名字
	repeated GameItem gis = 3;		//玩法列表
	optional string annoucement =4;		//公告
	optional int32 score = 5; //积分
};

message C2S_ClubTable
{
    optional int32 CurrentPage = 1;//当前页
};

message S2C_ClubTable
{
    repeated DeskItem dis = 1; 		//已经开局在玩的一些桌子
    repeated DeskItem unfullDesk = 2; 		//没坐满的一些桌子
    optional int32 TablePageCount = 3; //桌子总页数
};

enum DeskUpdateType
{
	DUT_NewPlayerIn = 1;
	DUT_PlayerOut = 2;
	DUT_PlayTimes = 3;
	DUT_RoomDestroy = 4;
	DUT_Begin = 5;
	DUT_Ready = 6;
};

message  UpdateDeskItem
{
	required int32 roomId = 1;
	optional int32 gameId = 2;
	optional int32 updateType = 3;
	optional PlayerItem pi = 4;
	optional int32 playerTimes = 5;
	optional int32 matchPlayerCount = 6;//匹配人数
}

message S2C_DeskItemChange
{
	repeated UpdateDeskItem updates = 1;
}

message ClubMember
{
	optional int32 id = 1;
	optional string name = 2;
	optional string icon = 3;
	optional int32 status = 4;
	optional int32 identity = 5;
	optional string remark = 6;
	optional int32 joinTime = 7;
	optional int32 score = 8;
}

message AgentClubMemberTotal
{
	required int32 agentPlayerId = 1;
	optional string agentName = 2;
	repeated ClubMember cms = 3;
}

message C2S_SearchMember
{
    optional bool isSearch = 1;//false表示查询所有，true表示查询直属成员搜索
    optional int32 userId = 2;//查询成员的id
}
//直属
message S2C_SearchMember
{
    repeated ClubMember cms = 1;
}
//全部
message S2C_SearchAllMember
{
    repeated AgentClubMemberTotal cams = 1;
}
